        sum[0] = true;
        for(auto&a:arr){
            auto prev = sum;
            for(int i=0;i<target;i++){
                if(i+a>target){
                    break;
                }
                sum[i+a] |= prev[i];
                if(sum[target]){
                    // cout<<"ans: "<<true<<"n";
                    return true;
                }
            }
        }
        // cout<<"ans: "<<false<<"n";
        return sum[target];
    }
    bool process(int mid, vector<int>& nums,vector<vector<int>>& queries){
        // cout<<mid<<"n";
        int n = nums.size();
        vector<vector<int>> target_arr(n);
        for(int i=0;i<mid;i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                target_arr[j].push_back(queries[i][2]);
            }
        }
        for(int i=0;i<n;i++){
            if(!targetSum(target_arr[i],nums[i])){
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size(),ans=INT_MAX;
        while(low <= high){
            int mid = low+(high-low)/2;
            if(process(mid,nums,queries)){
                high = mid-1;
                ans = mid;
            }else{
                low = mid+1;
            }
        }
        return ans==INT_MAX?-1:ans;
    }
};

