class Solution {
public:
    int findSum(vector<pair<int,int>>& arr, int target, int start, vector<vector<int>>& dp){
        if(start == arr.size()) return -1;

        if(dp[start][target] != -2) return dp[start][target];

        if(arr[start].first == target) return arr[start].second;

        int ans = -1;
        if(arr[start].first > target){
            ans = findSum(arr, target, start + 1, dp);
            return dp[start][target] = ans;
        }
        int a = findSum(arr, target - arr[start].first, start + 1, dp);
        int b = findSum(arr, target, start + 1, dp);
        if(a == -1 && b == -1) {
            if(ans == -1) ans = -1;
        }
        else if(a == -1) ans = b;
        else if(b == -1) ans = a;
        else ans = min(a, b);
        return dp[start][target] = ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        int n = nums.size();
        for(int i = 0; i < n; i++){
            if(nums[i] == 0) continue;

            vector<pair<int,int>> arr;
            for(int j = 0; j < queries.size(); j++){
                if(queries[j][0] <= i && i <= queries[j][1] && queries[j][2] <= nums[i]) arr.push_back({queries[j][2], j + 1});
            }
            vector<vector<int>> dp(arr.size(), vector<int>(nums[i] + 1, -2));
            int temp = findSum(arr, nums[i], 0, dp);
            if(temp == -1) return -1;
            ans = max(ans, temp);
        }
        return ans;
    }
};

