class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0;
        int r = queries.size();
        int res = -1;
        while(l<=r){
            int mid = l+(r-l)/2;
            if(valid(nums, queries, mid)){
                res = mid;
                r = mid-1;
            }
            else l = mid+1;
        }
        return res;
    }

    bool valid(vector<int>& nums, vector<vector<int>>& queries, int k){
        int n = nums.size();
        for (int j = 0; j < n; j++){
            int target = nums[j];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int i = 0; i<k; i++){
                int l = queries[i][0];
                int r = queries[i][1];
                int val = queries[i][2];
                if(l <= j && j <= r){
                    for (int s = target-val; s >= 0; s--){
                        if(dp[s]) dp[s+val] = true;
                    }
                }
            }
            if(!dp[target]) return false;
        }
        return true;
    }
};

