class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        def canZeroAfterK(k: int) -> bool:

            for j in range(n):
                target = nums[j]

                values = [val for l, r, val in queries[:k] if l <= j <= r]


                if target == 0:
                    continue


                dp = [False] * (target + 1)
                dp[0] = True
                for v in values:

                    for s in range(target, v - 1, -1):
                        if dp[s - v]:
                            dp[s] = True

                    if dp[target]:
                        break


                if not dp[target]:
                    return False
            return True


        if not canZeroAfterK(m):
            return -1


        low, high = 0, m
        ans = m
        while low <= high:
            mid = (low + high) // 2
            if canZeroAfterK(mid):
                ans = mid
                high = mid - 1
            else:
                low = mid + 1
        return ans

