class Solution {
public:
  vector<int> nums_;
  vector<vector<int>> queries_;
  void Dfs(int num_i, int q_i, int sum) {
    if (visited[num_i][q_i][sum]) {
      return ;
    }
    if (q_i == queries_.size()) {
      alg[num_i][q_i][sum] = true;
      visited[num_i][q_i][sum] = true;
      return ;
    }
    alg[num_i][q_i][sum] = true;
    // not choose
    Dfs(num_i, q_i + 1, sum);
    // choose if in queries[q_i]
    if (num_i >= queries_[q_i][0] && num_i <= queries_[q_i][1] && sum + queries_[q_i][2] < 2005) {
      Dfs(num_i, q_i + 1, sum + queries_[q_i][2]);
    }
    visited[num_i][q_i][sum] = true;
    return;
  }

  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    nums_ = nums;
    queries_ = queries;
    memset(visited, 0, sizeof(visited));
    memset(alg, 0, sizeof(alg));
    for (int num_i = 0; num_i < nums.size(); ++num_i) {
      // for (int q_i = 0; q_i <= queries.size(); ++q_i) {
      //   visited[num_i][q_i][0] = true;
      //   alg[num_i][q_i][0] = true;
      // }
      Dfs(num_i, 0, 0);
    }
    if (accumulate(nums.begin(), nums.end(), 0) == 0) {
      return 0;
    }
    for (int k = 1; k <= queries.size(); ++k) {
      bool all_zero = true;
      for (int num_i = 0; num_i < nums.size(); ++num_i) {
        if (alg[num_i][k][nums[num_i]] == false && nums[num_i] != 0) {
          all_zero = false;
          break;
        }
      }
      if (all_zero) {
        return k;
      }
    }
    return -1;
  }
};

