#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);
        init(dp, n);

        if (z(nums)) return 0;

        for (int i = 0; i < q.size(); i++) {
            aq(dp, nums, q[i]);
            if (allAchieved(dp, nums, n)) return i + 1;
        }
        return -1;
    }

private:
    void init(vector<bitset<1001>>& dp, int n) {
        for (int i = 0; i < n; i++) {
            dp[i].reset();
            dp[i].set(0);
        }
    }

    bool z(vector<int>& nums) {
        return all_of(nums.begin(), nums.end(), [](int x) { return x == 0; });
    }

    void aq(vector<bitset<1001>>& dp, vector<int>& nums, vector<int>& q) {
        int l = q[0], r = q[1], v = q[2];
        for (int i = l; i <= r; i++) {
            dp[i] |= (dp[i] << v);
            for (int j = nums[i] + 1; j < 1001; j++) {
                dp[i].reset(j);
            }
        }
    }

    bool allAchieved(vector<bitset<1001>>& dp, vector<int>& nums, int n) {
        for (int i = 0; i < n; i++) {
            if (!dp[i].test(nums[i])) return false;
        }
        return true;
    }
};

