#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);

        initializeDP(dp, n);
        if (checkAllZero(nums)) return 0;

        for (int k = 0; k < queries.size(); k++) {
            processQuery(dp, nums, queries[k]);

            if (isAchieved(dp, nums)) return k + 1;
        }
        return -1;
    }

private:
    void initializeDP(vector<bitset<1001>>& dp, int n) {
        for (int i = 0; i < n; i++) {
            dp[i].reset();
            dp[i].set(0, true);
        }
    }

    bool checkAllZero(vector<int>& nums) {
        for (int num : nums) {
            if (num != 0) return false;
        }
        return true;
    }

    void processQuery(vector<bitset<1001>>& dp, vector<int>& nums, vector<int>& query) {
        int l = query[0], r = query[1], v = query[2];

        for (int j = l; j <= r; j++) {
            dp[j] |= (dp[j] << v);
            for (int pos = nums[j] + 1; pos < 1001; pos++) {
                dp[j].reset(pos);
            }
        }
    }

    bool isAchieved(vector<bitset<1001>>& dp, vector<int>& nums) {
        for (int j = 0; j < nums.size(); j++) {
            if (!dp[j].test(nums[j])) return false;
        }
        return true;
    }
};

