class Solution {
public:
    vector<vector<int>> cache;
    int helper(int num, vector<vector<int>>& queries, int idx, int i, int curr) {
        if (curr == num) {
            return i;
        }
        if (i == queries.size()) return INT_MAX;
        if (cache[i][curr] != -1) return cache[i][curr];

        int l = queries[i][0], r = queries[i][1], val = queries[i][2];
        if (idx >= l && idx <= r) {
            if (curr + val > num) return cache[i][curr] = helper(num, queries, idx, i + 1, curr);

            return cache[i][curr] = min(helper(num, queries, idx, i + 1, curr), 
                                        helper(num, queries, idx, i + 1, curr + val));
        }
        return cache[i][curr] = helper(num, queries, idx, i + 1, curr);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        for (int i = 0; i < nums.size(); i++) {
            cache.assign(queries.size(), vector<int>(1001, -1));
            int a = helper(nums[i], queries, i, 0, 0);
            // cout<<i<<" "<<a<<" "<<endl;
            if (a == INT_MAX) return -1;
            res = max(res, a);
        }
        return res;
    }
};

