                dp[0][i] = 0;
            }
            for (int i = 1; i <= (int)a.size(); i++) {
                for (int sum = 1; sum <= target; sum++) {
                    dp[sum][i] = (dp[sum][i - 1]);
                    // db(sum, i, a[i-1].first);
                    if (a[i - 1].first <= sum) {
                        if (dp[sum - a[i - 1].first][i - 1] != inf) {
                            dp[sum][i] = min(dp[sum][i], a[i - 1].second);
                        }
                    }
                    // db(dp[sum][i]);
                }
            }
            // db(dp);
            return dp[target][(int)a.size()];
        };

        int ans = -1;
        for (int i = 0; i < n; i++) {
            int here = findIfSumPossible(nums[i], mp[i]);
            db(here);
            if (here == inf) {
                return -1;
            }
            ans = max(ans, here);
        }
        return ans;
    }
};

