class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, q = len(nums), len(queries)
        mxI = 0

        for i in range(n):
            if nums[i] == 0:
                continue

            dp = [False] * (nums[i] + 1)
            dp[0] = True
            for j in range(q):
                if queries[j][0] <= i <= queries[j][1] and queries[j][2] <= nums[i]:
                    v = queries[j][2]
                else:
                    continue

                for s in range(nums[i], v - 1, -1):
                    if dp[s - v]:
                        dp[s] = True

                if dp[nums[i]]:
                    mxI = max(mxI, j + 1)
                    break

            if dp[nums[i]] == False:
                return -1
        return mxI

