class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        max_val = max(nums)
        if max_val == 0:
            return 0  

        LIMIT = 1001
        ALL_ONES_MASK = (1 << LIMIT) - 1  

        dp = [(1 << 0) for _ in range(n)]  

        for k, (l, r, val) in enumerate(queries, start=1):
            shift_amount = val

            for i in range(l, r+1):
                old_mask = dp[i]
                shifted = (old_mask << shift_amount) & ALL_ONES_MASK
                dp[i] = old_mask | shifted

            all_zero = True
            for i in range(n):

                if nums[i] > max_val: 

                    all_zero = False
                    break
                if not (dp[i] & (1 << nums[i])):
                    all_zero = False
                    break

            if all_zero:
                return k  

        return -1

