class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),ans=0;
        rep(i,n)
        {
            if(nums[i]==0)
                continue;
            vpii pr;
            rep(j,(int)queries.size())
            {
                int u=queries[j][0],v=queries[j][1],w=queries[j][2];
                if(u<=i && i<=v)
                    pr.pb({j,w});
            }
            int tar=nums[i];
            vi dp(tar+1,INT_MAX);
            dp[0]=0;
            for(auto it:pr)
            {
                int key=it.F,val=it.S;
                nlloop(k,(tar-val),0)
                {
                    if(dp[k]!=INT_MAX)
                    {
                        int temp=max(dp[k],key+1);
                        dp[k+val]=min(dp[k+val],temp);
                    }
                }
            }
            if(dp[tar]==INT_MAX)
                return -1;
            ans=max(ans,dp[tar]);
        }
        return ans;
    }
};

