class Solution {
    public int minZeroArray(int[] nums, int[][] qs) {
        int n = nums.length, qn = qs.length;
        int maxOfMins = 0;
        for(int i=0; i<n; i++) {
            int min = solve(0, i, nums[i], qs, new Integer[qn][nums[i]+1]);
            maxOfMins = Math.max(maxOfMins, min);
        }

        return maxOfMins > qn? -1: maxOfMins;
    }

    private int solve(int i, int ni, int num, int[][] q, Integer[][] dp) {
        if(i == q.length) {
            if(num > 0) 
                return 100;
            return 0;
        }

        if(num == 0) return 0;

        if(dp[i][num] != null) {
            return dp[i][num];
        }

        int l = q[i][0], r = q[i][1], v = q[i][2];
        int min = solve(i + 1, ni, num, q, dp);
        if(l <= ni && r >= ni && num >= v) {
            min = Math.min(min, solve(i + 1, ni, num - v, q, dp));
        }

        return dp[i][num] = min + 1;
    }
}

