    public int minZeroArray(int[] nums, int[][] queries) {
        int sum = Arrays.stream(nums).sum();
        if(sum == 0) return 0;
        int n = nums.length;
        List<int[]>[] lists = new List[n];
        for(int i = 0; i < n; i++) lists[i] = new ArrayList<>();
        for(int i = 0; i < queries.length; i++) {
            int[] query = queries[i];
            int l = query[0], r = query[1], val = query[2];
            for(int j = l; j <= r; j++) {
                lists[j].add(new int[]{val, i});
            }
        }
        int res = Integer.MIN_VALUE;
        for(int i = 0; i < n; i++) {
            int t = helper(lists[i], nums[i]);
            // System.out.println("t: " + t);
            if(t == -1) return -1;
            res = Math.max(res, t);
        }
        return res;
    }


    int helper(List<int[]> list, int target) {
        int n = list.size();
        // list.forEach((e) -> System.out.print(Arrays.toString(e)));
        // System.out.println(", target: " + target);
        boolean[][] dp = new boolean[n + 1][target + 1];
        dp[0][0] = true;

        for(int i = 1; i <= n; i++) {
            int[] cur = list.get(i - 1);
            int val = cur[0], index = cur[1];
            for(int j = 0; j <= target; j++) {
                dp[i][j] |= dp[i - 1][j];
                if(j - val >= 0) dp[i][j] |= dp[i - 1][j - val];
            }
            if(dp[i][target]) return index + 1;
        }

        return -1;
    }

