impl Solution {
    pub fn min_zero_array(nums: Vec<i32>, queries: Vec<Vec<i32>>) -> i32 {
        let n = nums.len();
        let mut ans = vec![i32::MAX; n];
        for i in 0..n {
            let max = nums[i] as usize;
            let mut dp = vec![i32::MAX; max+1];
            dp[max] = 0;
            for qi in 0..queries.len() {
                let q = &queries[qi];
                let (l, r, val) = (q[0] as usize, q[1] as usize, q[2] as usize);
                if i < l || r < i {
                    continue;
                }
                for j in val..=max {
                    if dp[j] != i32::MAX {
                        dp[j-val] = dp[j-val].min(qi as i32 +1);
                    }
                }
            }
            ans[i] = dp[0];
        }
        let ansmax = ans.iter().max().unwrap();
        if *ansmax == i32::MAX {
            -1
        } else {
            *ansmax
        }
    }
}

