class Solution {
public:

    bool r(vector<int> &vals,int x) {
        vector<bool> dp(x+1,0);
        dp[0]=1;
        for (auto v:vals) {
            for (int j=x;j>=v;j--) {
                dp[j] = (dp[j]||dp[j-v]);
            }
        }
        return dp[x];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();

        int s=0,e=q,ans=-1;
        while(s<=e) {
            int mid=s+(e-s)/2;

            bool f=1;
            for (int i=0;i<n;++i) {
                vector<int> v;
                for (int j=0;j<mid;++j) {
                    if ((queries[j][0]<=i)&&(i<= queries[j][1]))
                        v.push_back(queries[j][2]);
                }
                if(!r(v,nums[i])){
                    f=0;break;
                }
            }

            if (f) {
                ans=mid;
                e=mid-1;
            } else {
                s=mid+1;
            }
        }

        return ans;
    }
};

