    int minZeroArray(vector<int>&a, vector<vector<int>>&q) {
        int n=a.size(),m=q.size();
        int flag=1;
        int i;
        for(i=0;i<n;i++)
            {
                if(a[i]!=0)
                {
                    flag=0;
                    break;
                }
            }
        if(flag)
            return 0;
        vector<vector<pair<int,int>>>v(n);
        int j;
        for(j=0;j<m;j++)
            {
                int l=q[j][0],r=q[j][1],val=q[j][2];
                for(i=l;i<=r;i++)
                    {
                        v[i].push_back({j,val});
                    }
            }

        for(i=0;i<n;i++)
            {
                sort(v[i].begin(), v[i].end(), 
                 [](const pair<int,int>& a, const pair<int,int>& b){
                     return a.first < b.first;
                 });
            }

        int l=1;
        int h=m+1;
        while(l<h)
            {
                int mid=l+(h-l)/2;
                flag=1;
                for(i=0;i<n;i++)
                    {
                        int t=a[i];
                        bitset<1001>dp;
                        dp.reset();
                        dp[0]=1;
                        for(auto &it:v[i])
                            {
                                if(it.first>=mid)
                                    break;
                                int t1=it.second;
                                dp|=(dp<<t1);
                                if(dp[t])break;
                            }
                        if(!dp[t])
                        {
                            flag=0;
                            break;
                        }
                    }
                if(flag)
                {
                    h=mid;
                }
                else
                {
                    l=mid+1;
                }
            }
        if(l>m)
            return -1;
        return l;
    }
};

