            FOR(i, q[0], q[1] + 1)
            {
                for (int j = M; j >= 0; j--)
                {
                    if (j + q[2] > M)
                        continue;
                    if (dp[i][j])
                    {
                        dp[i][j + q[2]] = true;
                    }
                }
            }
            bool isOK = true;
            REP(i, N)
            {
                if (!dp[i][nums[i]])
                {
                    isOK = false;
                }
            }
            if (isOK)
            {
                return ans;
            }
        }
        return -1;
    }
