class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(queries)

        def getMinQueries(idx, val):
            A = [[False] * (val + 1) for _ in range(n + 1)]     
            A[0][val] = True

            for i in range(1, n + 1):
                l, r, dec = queries[i - 1]
                for j in range(val + 1):
                    if l <= idx <= r and j + dec <= val:
                        A[i][j] |= A[i - 1][j + dec]
                    A[i][j] |= A[i - 1][j]
                    # print(idx, val, i, j, A[i][j])
                if A[i][0] == True:
                    return i
            return -1

        allZero = True
        for val in nums:
            if val != 0:
                allZero = False
        if allZero:
            return 0

        ans = -1
        for idx, val in enumerate(nums):
            res = getMinQueries(idx, val)
            if res == -1:
                return -1
            ans = max(ans, res)
        return ans

