class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        element_queries = [[] for _ in range(n)]
        for j, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                element_queries[i].append((j, val))

        required_ks = []
        for i in range(n):
            current_num = nums[i]
            applicable = element_queries[i]

            if current_num == 0:
                required_ks.append(0)
                continue

            sum_vals = sum(val for _, val in applicable)
            if sum_vals < current_num:
                return -1
            applicable.sort()
            dp = {0: -1} 
            for j_original, val in applicable:
                temp = {}
                for s in list(dp.keys()):
                    new_sum = s + val
                    if new_sum not in dp:
                        temp[new_sum] = j_original
                for s in temp:
                    if s not in dp or temp[s] < dp.get(s, float('inf')):
                        dp[s] = temp[s]

            if current_num not in dp:
                return -1
            required_j = dp[current_num]
            required_ks.append(required_j + 1)
        max_k = max(required_ks)
        if max_k > len(queries):
            return -1
        return max_k

