class Solution {
public:
    int func(vector<int>& contri,int target,int idx,vector<vector<int>>& dp){
        if(target==0){
            return true;
        }
        if(idx>=contri.size()){
            return false;
        }
        if(dp[idx][target]!=-1){
            return dp[idx][target];
        }
        bool pick=false;
        if(contri[idx]<=target){
            pick |= func(contri,target-contri[idx],idx+1,dp);
        }
        bool notpick=func(contri,target,idx+1,dp);
        return dp[idx][target]=(pick || notpick);
    }
    int f(int target,vector<int>& contri){
        int n=contri.size();
        vector<vector<int>> dp(n, vector<int>(target + 1, -1));
        return func(contri,target,0,dp);
    }

    bool isPos(vector<int>& nums, vector<vector<int>>& queries, int tmp){
        int n=nums.size();
        for(int i=0;i<n;i++){
            if(nums[i]==0){
                continue;
            }
            vector<int> contri;
            for(int j=0;j<tmp;j++){
                if(i>=queries[j][0] && i<=queries[j][1]){
                    contri.push_back(queries[j][2]);
                }
            }
            if(!f(nums[i],contri)){
                return false;
            }
        }
        return true;
    }    

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int s=0,e=queries.size();
        int ans=-1;
        while(s<=e){
            int m=s+(e-s)/2;
            if(isPos(nums,queries,m)){
                ans=m;
                e=m-1;
            }else{
                s=m+1;
            }
        }
        return ans;
    }
};

