class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int[][] dp = new int[n][1001];

        for (int j = 0; j < n; j++) {
            Arrays.fill(dp[j], 0);
            dp[j][0] = 1;
        }

        boolean all_zero = true;
        for (int num : nums) {
            if (num != 0) {
                all_zero = false;
                break;
            }
        }
        if (all_zero)
            return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                int[] newDp = new int[1001];
                for (int i = 0; i < 1001; i++) {
                    if (dp[j][i] == 1) {
                        if (i + v < 1001) {
                            newDp[i + v] = 1;
                        }
                    }
                }
                for (int i = 0; i < 1001; i++) {
                    dp[j][i] |= newDp[i];
                }

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j][pos] = 0;
                }
            }

            boolean valid = true;
            for (int j = 0; j < n; j++) {
                if (dp[j][nums[j]] == 0) {
                    valid = false;
                    break;
                }
            }
            if (valid)
                return k + 1;
        }
        return -1;
    }
}

