public:
  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    using sum_t = bitset<11000>;
    vector<sum_t> tab(n, sum_t(1));

    auto canDo = [&] {
      for (int i = 0; i < n; ++i) {
        if (!tab[i].test(nums[i])) {
          return false;
        }
      }
      return true;
    };
    if (canDo()) {
      return 0;
    }

    for (int i = 0; i < (int)queries.size(); ++i) {
      int l = queries[i][0], r = queries[i][1], v = queries[i][2];
      for (int p = l; p <= r; ++p) {
        auto& sum = tab[p];
        sum |= (sum << v);
      }
      if (canDo()) {
        return i + 1;
      }
    }
    return -1;
  }
};

