class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int n = nums.size();
        vector<set<int>> vs(n);
        int zeros = 0;
        for (int i = 0; i < n; ++i) {
            if (!nums[i]) {
                zeros++;
            }
        }
        if (zeros == n)
            return 0;
        for (int i = 0; i < q; ++i) {
            // cout<<"EEE"<<endl;
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            bool works = true;
            for (int j = l; j <= r; ++j) {
                if (nums[j] == 0 || vs[j].count(nums[j]))
                    continue;
                if (vs[j].size() == 0) {
                    vs[j].insert(val);
                    if (val == nums[j])
                        zeros++;
                } else {
                    set<int> s;
                    for (auto x : vs[j]) {
                        s.insert(x + val);
                    }
                    for (auto x : s) {
                        vs[j].insert(x);
                    }
                    vs[j].insert(val);
                    if (vs[j].count(nums[j])) {
                        zeros++;
                    }
                }
            }
            if (zeros == n)
                return i + 1;
        }
        return -1;
    }
};

