#include <vector>
#include <algorithm>
using namespace std;

class Solution {
private:
    // Custom class to store all variables
    class MinimumOperationsCalculator {
    public:
        // Input data
        vector<int> originalArray;
        vector<vector<int>> operationQueries;
        int arraySize;
        int queryCount;

        // Constructor
        MinimumOperationsCalculator(vector<int>& inputArray, vector<vector<int>>& queries) {
            originalArray = inputArray;
            operationQueries = queries;
            arraySize = inputArray.size();
            queryCount = queries.size();
        }

        // Check if we can reduce array to zero using first k queries
        bool canMakeZero(int k) {
            // Check each element in the array
            for (int position = 0; position < arraySize; position++) {
                // Dynamic programming array to track possible sums
                vector<bool> possibleSums(1001, false);
                possibleSums[0] = true;  // Base case: we can always make sum of 0

                // Apply each of the first k queries that affect current position
                for (int queryIndex = 0; queryIndex < k; queryIndex++) {
                    // Check if query applies to current position
                    if (position >= operationQueries[queryIndex][0] && 
                        position <= operationQueries[queryIndex][1]) {

                        // Value to subtract from current element
                        int subtractValue = operationQueries[queryIndex][2];

                        // Update possible sums (iterate backwards to avoid counting same operation multiple times)
                        for (int sum = 1000 - subtractValue; sum >= 0; sum--) {
                            if (possibleSums[sum]) {
                                possibleSums[sum + subtractValue] = true;
                            }
                        }
                    }
                }

                // Check if we can reduce current element to zero
                if (!possibleSums[originalArray[position]]) {
                    return false;
                }
            }

            return true;
        }

        // Binary search to find minimum number of queries
        int findMinimumQueries() {
            int lowerBound = 0;
            int upperBound = queryCount;
            int result = -1;

            while (lowerBound <= upperBound) {
                int middle = (lowerBound + upperBound) / 2;

                if (canMakeZero(middle)) {
                    result = middle;
                    upperBound = middle - 1;  // Try fewer queries
                } else {
                    lowerBound = middle + 1;  // Need more queries
                }
            }

            return result;
        }
    };

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // Create calculator object and find minimum queries needed
        MinimumOperationsCalculator calculator(nums, queries);
        return calculator.findMinimumQueries();
    }
};

