class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        index_map = defaultdict(list)
        index_pos = defaultdict(list)
        n = len(nums)
        n_q = len(queries)

        def solve(x, arr):
            if x == 0:
                return -1
            n = len(arr)
            dp = [[False] * (1 + x) for i in range(n + 1)]
            dp[0][0] = True
            for i in range(1, n + 1):
                for j in range(1 + x):
                    if j >= arr[i - 1]:
                        dp[i][j] = dp[i - 1][j] or dp[i - 1][j - arr[i - 1]]
                    else:
                        dp[i][j] = dp[i - 1][j]
                if dp[i][x] == True:
                    return i - 1
            return n

        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r + 1):
                index_map[j].append(val)
                index_pos[j].append(i)

        ans = -1
        for i in range(n):
            if nums[i] == 0:
                continue
            arr = index_map[i]
            ind = solve(nums[i], arr)
            if ind >= len(arr):
                return -1
            ans = max(ans, index_pos[i][ind])
        return ans + 1

