class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        v = [[] for i in range(len(nums))]

        # splitting into values at their indexes
        for i, q in enumerate(queries):
            l, r, val = q
            for j in range(l, r+1):
                v[j].append((val, i+1))

        # performing knapsack on every value
        def knapsack(ceiling, values):
            dp = {0}
            for i in values:
                new_dp = dp.copy()
                for j in dp:
                    s = j + i[0]
                    if s < ceiling:
                        new_dp.add(s)
                    elif s == ceiling:
                        return i[1]
                    # else it's larger and we don't need to keep track of it
                dp = new_dp
            return -1

        mins = []
        # print(v)
        for i, x in enumerate(v):
            if nums[i] == 0:
                mins.append(0)
                continue
            r = knapsack(nums[i], x)
            # print(r)
            if r == -1:
                return -1
            else:
                mins.append(r)

        return max(mins)

