class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int mq = queries.size();
        vector<vector<pair<int, int>>> Q(n); // For each index i, list of (query index, val) of queries that include i

        // Populate Q[i] with queries that include index i
        for (int j = 0; j < mq; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; ++i) {
                Q[i].emplace_back(j, val);
            }
        }

        // Sort each Q[i] by query index
        for (int i = 0; i < n; ++i) {
            sort(Q[i].begin(), Q[i].end(), [](const pair<int, int>& a, const pair<int, int>& b) {
                return a.first < b.first;
            });
        }

        // Precompute prefix sums and possible subset sums for each Q[i]
        vector<vector<int>> prefixSums(n);
        vector<vector<vector<bool>>> subsetSums(n);

        for (int i = 0; i < n; ++i) {
            const auto& queries_i = Q[i];
            int m = queries_i.size();
            prefixSums[i].resize(m + 1, 0);
            for (int j = 0; j < m; ++j) {
                prefixSums[i][j + 1] = prefixSums[i][j] + queries_i[j].second;
            }

            // Compute subset sums using dynamic programming
            vector<vector<bool>> dp(m + 1);
            dp[0] = vector<bool>(1, false);
            dp[0][0] = true;

            for (int j = 0; j < m; ++j) {
                int val = queries_i[j].second;
                int prevSize = dp[j].size();
                dp[j + 1].resize(prevSize + val, false);

                // Copy previous possibilities
                for (int s = 0; s < prevSize; ++s) {
                    if (dp[j][s]) {
                        // Not take the current value
                        if (s < dp[j + 1].size()) {
                            dp[j + 1][s] = true;
                        }
                        // Take the current value
                        if (s + val < dp[j + 1].size()) {
                            dp[j + 1][s + val] = true;
                        }
                    }
                }
            }
            subsetSums[i] = move(dp);
        }

        // Check for each k from 0 to mq
        for (int k = 0; k <= mq; ++k) {
            bool valid = true;
            for (int i = 0; i < n; ++i) {
                const auto& queries_i = Q[i];
                int m = upper_bound(queries_i.begin(), queries_i.end(), make_pair(k - 1, 0),
                    [](const pair<int, int>& a, const pair<int, int>& b) {
                        return a.first < b.first;
                    }) - queries_i.begin();

                int sum = prefixSums[i][m];
                if (sum < nums[i]) {
                    valid = false;
                    break;
                }
                int D = sum - nums[i];
                if (D < 0) {
                    valid = false;
                    break;
                }

                if (m >= subsetSums[i].size() || D >= subsetSums[i][m].size() || !subsetSums[i][m][D]) {
                    valid = false;
                    break;
                }
            }
            if (valid) {
                return k;
            }
        }

        return -1;
    }
};

