class Solution {
public:
    int dpcal[1001][1001];
    int dp(int idx,int sum,int val,vector<int>&vec)
    {
      if(idx==vec.size())
      {
          if(sum==val)
          {
              return 0;
          }
          else
             return 1e4;
      }
        if(sum>val)
        {
            return 1e4;
        }
        if(dpcal[idx][sum]!=-1)
            return dpcal[idx][sum];
      int ans=1e4;
      ans=min(ans,1+dp(idx+1,sum+vec[idx],val,vec));
      ans=min(ans,dp(idx+1,sum,val,vec));
        dpcal[idx][sum]=ans;
       // cout<<ans<<"n";
      return ans;
    }
    int solve(int mid,vector<int>&nums,vector<vector<int>>&queries)
    {
        int n=nums.size();
        map<int,vector<int>>cnt;
        for(int i=0;i<mid;i++)
        {
            int left=queries[i][0];
            int right=queries[i][1];
            int val=queries[i][2];
            for(int j=left;j<=right;j++)
            {
                cnt[j].push_back(val);
            }
        }

        memset(dpcal,-1,sizeof dpcal);
        bool flag=true;
        for(int i=0;i<n;i++)
        {
            vector<int>vec=cnt[i];
            int ans=dp(0,0,nums[i],vec);
         //   cout<<ans<<" ";
            if(ans==1e4)
            {
                flag=false;
                break;
            }
            //cout<<"n";
            memset(dpcal,-1,sizeof dpcal);
        }
        return flag;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) 
    {
      int n=nums.size();
      int sz=queries.size();
      int l=0;
      int r=sz;
      int ans=-1;
      while(l<=r)
      {
          int mid=l+(r-l)/2;
          if(solve(mid,nums,queries))
          {
             // cout<<mid<<"n";
            ans=mid;
            r=mid-1;
          }
          else
             l=mid+1;
      }
        return ans;
    }
};

