class Solution {
public:
    int rec(int k,vector<int>& nums, vector<vector<int>>& queries){
        int n = nums.size(),m = queries.size();
        for(int i=0;i<n;i++){
            if(nums[i] == 0) continue;
            vector<int>temp;
            for(int j=0;j<k;j++){
                int l = queries[j][0],r = queries[j][1],val = queries[j][2];
                if(l<=i && i<=r){
                    temp.push_back(val);
                }
            }
            vector<int>dp(nums[i]+1,0);
            dp[0] = 1;
            for(auto &it : temp){
                for(int c = nums[i];c >= it;c--){
                    if(dp[c - it]){
                        dp[c] = 1;
                    }
                }
            }
            if(!dp[nums[i]]) return 0;
        }
        return 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(),m = queries.size();
        int s = 0,e = m;
        int ans = -1;
        while(s <= e){
            int mid = (s + e)/2;
            if(rec(mid,nums,queries)){
                ans = mid;
                e = mid - 1;
            }
            else{
                s = mid + 1;
            }
        }
        return ans;
    }
};

