class Solution {
public:
    int dp[1001][1001];
    int rec(vector<int>&arr, int i, int left){
        int n = arr.size();
        if(left < 0) return 0;
        if(i == n) return left==0;

        if(dp[i][left] != -1) return dp[i][left];

        int ans = rec(arr, i + 1, left);
        if(arr[i] <= left){
            ans = ans or rec(arr, i+1, left - arr[i]);
        }

        return dp[i][left] = ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        function<bool(int)> check = [&](int mid) {
            int n = nums.size();
            vector<vector<int> > arr(n);

            for(int i = 0; i < mid; i++){ // 1e3
                int l = queries[i][0]; int r = queries[i][1]; 
                int val = queries[i][2];

                for(int j=l;j<=r;j++) arr[j].push_back(val);
            } 

            for(int i = 0; i < n; i++) {
                memset(dp, -1, sizeof(dp));
                if(!rec(arr[i], 0, nums[i])) return false;
            }

            return true;
        };

        int n = queries.size();
        int lo = 0;
        int hi = n;
        int ans = n + 1; 

        while(lo <=hi){
            int mid = (lo + hi)/2;
            // ffffffttttttt
            if(check(mid)) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }

        return ans == n + 1 ? -1: ans;
    }
};

