class Solution {
public:
    bool isSubsetSum(vector<int>& nums, int sum) {
        int n = nums.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));

        for (int i = 0; i <= n; i++) dp[i][0] = true;

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= sum; j++) {
                if (nums[i - 1] <= j)
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - nums[i - 1]];
                else
                    dp[i][j] = dp[i - 1][j];
            }
        }

        return dp[n][sum];
    }
    bool check(vector<int> nums, vector<vector<int>>& queries, set<int> pos_idx, int x){
        vector<vector<int>> indexes(nums.size());
        for(int i=0; i<x; ++i){
            for(int j=queries[i][0]; j<=queries[i][1]; ++j){
                if(nums[j] == 0) continue;
                indexes[j].push_back(queries[i][2]);
                if(queries[i][2] == nums[j]){
                    nums[j] = 0;
                    pos_idx.erase(j);
                }
            }
        }
        for(int i=0; i<nums.size(); ++i){
            if(nums[i] == 0) continue;
            if(isSubsetSum(indexes[i], nums[i])){
                pos_idx.erase(i);
            }
        }
        return pos_idx.size() == 0;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        set<int> pos_idx, temp;
        for(int i=0; i<nums.size(); ++i){
            if(nums[i] > 0){
                pos_idx.insert(i);
            }
        }
        if(pos_idx.size() == 0) return 0;
        int l = 1, r = queries.size(), res = INT_MAX;
        while(l<=r){
            int mid = (l+r)/2;
            temp = pos_idx;
            if(check(nums, queries, temp, mid)){
                r = mid - 1;
                res = min(res, mid);
            }else{
                l = mid + 1;
            }
        }
        return (res == INT_MAX) ? -1 : res;
    }
};

