class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if (zeroArray(nums)) return 0;

        int[][] temp = queries;
        int n = nums.length;

        boolean[][] dp = new boolean[n][1001];
        for (int i=0; i < n; i++) dp[i][0] = true;

        for (int i=0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for (int j=l; j<= r; j++) {
                for (int k=1000; k>= val; k--) {
                    if (dp[j][k-val]) {
                        dp[j][k] = true;
                    }
                }
            }

            boolean allZero = true;
            for (int j=0; j < n; j++) {
                if (nums[j] <= 1000) {
                    if (!dp[j][nums[j]]) {
                        allZero = false;
                        break;
                    }
                } else {
                    allZero = false;
                    break;
                }
            }

            if (allZero) return i + 1;
        }

        return -1;
    }

    private boolean zeroArray(int[] arr) {
        for (int x: arr) {
            if (x != 0) return false;
        }
        return true;
    }
}

