             vector<vector<int>> curr = dp;

             for(int i = it[0]; i <= it[1]; i++) {
                 if (mp.count(i)) 
                     continue;

                 for(int j = 0; j <= nums[i]; j++) {
                     if (dp[i][j] == 1 && j + it[2] <= nums[i]){
                         curr[i][j + it[2]] = 1;
                     }
                 }
                 if(curr[i][nums[i]] == 1){
                     mp[i] = 1;
                 }
             }
             dp = curr; 

             if(mp.size() == nums.size()) 
                 return ind;
         }
         return -1;
    }
};

