class Solution {
    private int[][] dp;

    private boolean hasSum(List<Integer> list, int idx, int sum){
        if (sum < 0){
            return false;
        }
        if (sum == 0){
            return true;
        }
        if (idx == list.size()){
            return false;
        }
        //System.out.println(list.size());
        //System.out.println(idx + " " + sum);
        if (dp[idx][sum] == -1) return false;
        if (dp[idx][sum] == 1001) return true;

        if (hasSum(list, idx + 1, sum) || hasSum(list, idx + 1, sum - list.get(idx))){
            dp[idx][sum] = 1001;
            return true;
        } else {
            dp[idx][sum] = -1;
            return false;
        }        
    }

    private boolean isValid(int[] arr, int[][] q, int num){
        List<List<Integer>> aux = new ArrayList<List<Integer>>();
        for (int i = 0; i < arr.length; i++){
            aux.add(new ArrayList<Integer>());
        }

        for (int i = 0; i < num; i++){
            for (int j = q[i][0]; j <= q[i][1]; j++){
                aux.get(j).add(q[i][2]);
            }
        }

        for (int i = 0; i < arr.length; i++){
            dp = new int[aux.get(i).size() + 1][arr[i] + 1];
            //System.out.println(arr[i]);
            if (!hasSum(aux.get(i), 0, arr[i])) return false;
        }

        return true;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int lo = 0;
        int hi = queries.length;

        if (isValid(nums, queries, hi)){
            int mid = 0;
            while (lo < hi){
                mid = (lo + hi) / 2;
                if (isValid(nums, queries, mid)){
                    hi = mid;
                } else {
                    lo = mid + 1;
                }
            }

            mid = (lo + hi) / 2;
            return mid;
        }

        return -1;
    }
}

