    bool f(int mid , vector<int>& nums,vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        for (int j = 0; j < n; j++) {
            bitset<1001> dp;
            dp[0] = 1;
            for (int i = 0; i < mid; i++) {
                if (queries[i][0] <= j && j <= queries[i][1]) {
                    dp |= (dp << queries[i][2]);
                }
            }
            if (!dp.test(nums[j]))
                return false;
        }
        return true;
    }


public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int sum = 0 ;
        int n = nums.size();
        int m = queries.size();
        for(auto i : nums){
            sum += i;
        }
        if(sum == 0){
            return 0 ;
        }

        int low = 1 ;
        int high = m ;
        int ans = -1;

        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (f(mid , nums, queries)) {
                ans = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        return ans;
    }
};

