        m = len(queries)

        @cache
        def dp(i, num, idx):
            if idx == m:
                return m+1
            take = notake = float(inf)
            if queries[idx][0] <= i <= queries[idx][1] and num >= queries[idx][2]:
                left = num - queries[idx][2]
                take = idx+1 if left == 0 else dp(i, left, idx+1)

            notake = dp(i, num, idx+1)
            return min(take, notake)

        ans = 0
        for i, num in enumerate(nums):
            if num == 0: continue
            v = dp(i, num, 0)

            if v == m+1:
                return -1
            ans = max(v, ans)

        return ans

