import java.util.BitSet;

public class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] dp = new BitSet[n];

        // Initialize the BitSet array
        for (int j = 0; j < n; j++) {
            dp[j] = new BitSet(1001);
            dp[j].set(0); // Start with 0 being achievable
        }

        // Check if the array is already all zeros
        boolean allZero = true;
        for (int j = 0; j < n; j++) {
            if (nums[j] != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        // Process each query
        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            // Apply the query to the range [l, r]
            for (int j = l; j <= r; j++) {
                // Shift the bits to the left by v positions
                BitSet newBits = new BitSet(1001);
                for (int pos = dp[j].nextSetBit(0); pos != -1; pos = dp[j].nextSetBit(pos + 1)) {
                    if (pos + v < 1001) {
                        newBits.set(pos + v);
                    }
                }
                dp[j].or(newBits); // Combine the new bits with the existing ones
            }

            // Check if all positions are achieved
            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }
            if (allAchieved) return k + 1;
        }

        return -1;
    }
}

