class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]):
        min_k = [] # min k for each element

        for i, num in enumerate(nums):
            k = 0
            if num == 0:
                min_k.append(0)
                continue

            possible_sums = {0}
            for left, right, val in queries:      
                k += 1
                if i < left or i > right:
                    continue

                new_sums = {s + val for s in possible_sums}
                possible_sums.update(new_sums)
                if num in possible_sums:
                    min_k.append(k)
                    break

        if len(min_k) < len(nums):
            return -1
        return max(min_k)

