class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), qn = queries.size();
        int mx = *max_element(nums.begin(), nums.end());
        if(mx == 0) return 0;

        int ans = 0;
        for(int i=0; i<n; ++i){
            if(nums[i] == 0) continue;

            set<int> s;
            bool found = 0;
            for(int q=0; q<qn; ++q){
                int l = queries[q][0], r = queries[q][1], val = queries[q][2];
                if(i >= l && i <= r){
                    for(auto it: set<int>(s.begin(), s.end())){
                        if(it+val <= nums[i])
                            s.insert(it+val);
                        if(it+val == nums[i]){
                            ans = max(ans, q);
                            found = 1;
                            break;
                        }
                    }

                    if(found) break;

                    s.insert(val);
                    if(val == nums[i]){
                        ans = max(ans, q);
                        found = 1;
                        break;
                    }
                }
            }

            if(!found) return -1;
        }

        return ans+1;
    }
};

