class Solution {
public:
    bool helper(vector<int>& nums, int target, int index, vector<vector<int>>& dp) {
        if (target == 0) return true;
        if (index < 0) return false;
        if (dp[index][target] != -1) return dp[index][target];

        bool include = (target >= nums[index]) && helper(nums, target - nums[index], index - 1, dp);
        bool exclude = helper(nums, target, index - 1, dp);

        return dp[index][target] = include || exclude;
    }

    bool func(vector<int>& nums, vector<vector<int>>& queries, int k) {
        bool ans = true;

        for (int i = 0; i < nums.size(); i++) {
            if (nums[i] == 0) continue;

            vector<int> arr;
            for (int j = 0; j < k; j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) arr.push_back(queries[j][2]);
            }

            if (arr.empty()) return false;

            int maxTarget = nums[i];  
            vector<vector<int>> dp(arr.size(), vector<int>(maxTarget + 1, -1));

            ans = ans & helper(arr, maxTarget, arr.size() - 1, dp);
        }

        return ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int s = 0, e = queries.size(), ans = -1;

        while (s <= e) {
            int m = s + (e - s) / 2;
            if (func(nums, queries, m)) {
                ans = m;
                e = m - 1;
            } else {
                s = m + 1;
            }
        }

        return ans;
    }
};

