class Solution {
public:
    bool solve(int ind,vector<int>& arr,int n, int target,vector<vector<int>> &dp){

        if(target < 0) return false;
        if(target == 0) return true;
        if(ind >= n) return false;

        if(dp[ind][target] != -1) return dp[ind][target];

        bool take = solve(ind+1,arr,n,target-arr[ind],dp); 
        bool ntake = solve(ind+1,arr,n,target,dp);

        return dp[ind][target] = take|ntake;

    }
    bool check(vector<int> arr,int target){
        int n = arr.size();
        vector<vector<int>> dp(n,vector<int>(target+1,-1));
        return solve(0,arr,n,target,dp);
    }
    bool pos(vector<int> nums, vector<vector<int>> qrs,int m){
        int n = nums.size();
        bool fl = true;
        for(int i=0;i<n;i++){
            if(nums[i] != 0) fl = false;
        }
        if(fl) return fl;

        vector<vector<int>> v(n);
        for(int i=0;i<m;i++){
            for(int j = qrs[i][0];j<=qrs[i][1];j++){
                v[j].push_back(qrs[i][2]);
            }
        }

        for(int i=0;i<n;i++){
            if(!check(v[i],nums[i])) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qrs) {

        int l = 0;
        int r = qrs.size();

        int ans = -1;
        int m = (l+r)/2;
        while(l <= r){
            if( pos(nums,qrs,m) ){
                ans = m;
                r = m-1;
            }
            else{
                l = m+1;
            }
            m = (l+r)/2;
        }

        return ans;
    }
};

