class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

       int u = 0; 
       int n = queries.length;
       for(int i=0; i<nums.length; i++) {
           Integer[][] dp = new Integer[n+1][nums[i]+1];
           int ans = solve(i, nums[i], 0, queries, dp);
           if(ans == 2000) return -1;
           //System.out.println(ans);
           u = Math.max(u, ans);
       }

       return u;
    }

    int solve(int index, int rem, int i, int[][] queries, Integer dp[][]) {

        if(rem == 0) return i;

        if(i >= queries.length) return 2000;

        if(dp[i][rem] != null) return dp[i][rem];

        int ans = 2000;

        if(index >= queries[i][0] && index <= queries[i][1]) {

            if(rem >= queries[i][2]) {
                ans = Math.min(ans,solve(index, rem - queries[i][2], i+1, queries, dp));
            }
        }

        ans = Math.min(ans, solve(index, rem, i+1, queries, dp));

        return dp[i][rem] = ans;
    }
}

