using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& elements, vector<vector<int>>& operations) {
        int size = elements.size();
        int opsCount = operations.size();
        vector<vector<int>> opsForIndex(size);  
        for (int opIdx = 0; opIdx < opsCount; ++opIdx) {
            int left = operations[opIdx][0];
            int right = operations[opIdx][1];
            for (int i = left; i <= right; ++i) {
                opsForIndex[i].push_back(opIdx);
            }
        }
        vector<int>visited(operations.size(),-1);
        int maxSteps = 0;
        for (int i = 0; i < size; ++i) {
            int targetValue = elements[i];
            if (targetValue == 0) continue;

            vector<int>& applicableOps = opsForIndex[i];
            unordered_map<int, int> dpMap;
            dpMap[0] = -1;

            for (int opIndex : applicableOps) {
                int addValue = operations[opIndex][2];
                unordered_map<int, int> tempMap;

                for (auto& entry : dpMap) {
                    int currentSum = entry.first;
                    int highestOpIdx = entry.second;

                    int newSum = currentSum + addValue;
                    if (newSum > targetValue) continue;

                    int newOpIdx = max(highestOpIdx, opIndex);
                    if (tempMap.find(newSum) == tempMap.end()) {
                        tempMap[newSum] = newOpIdx;
                    } else {
                        if (newOpIdx < tempMap[newSum]) {
                            tempMap[newSum] = newOpIdx;
                        }
                    }
                }


                for (auto& entry : tempMap) {
                    int sumKey = entry.first;
                    int minOpIdx = entry.second;
                    if (dpMap.find(sumKey) == dpMap.end() || minOpIdx < dpMap[sumKey]) {
                        dpMap[sumKey] = minOpIdx;
                    }
                }
            }

            if (dpMap.find(targetValue) == dpMap.end()) {
                return -1;
            }

            int requiredSteps = dpMap[targetValue] + 1;
            if (requiredSteps > maxSteps) {
                maxSteps = requiredSteps;
            }
        }

        return maxSteps;
    }
};

