class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        left = 0
        right = len(queries)
        minK = float('inf')
        while left <= right:
            mid = (left + right) // 2
            # -we dont have to decrement at all
            # -build arr, arr[i] == list of possible decrements 
            # for ith index of nums
            # -combo them together until we reach exactly
            # nums[i]
            arr = []
            for num in nums:
                arr.append([])
            for i in range(mid):
                l,r,val = queries[i]
                for i in range(l, r+1):
                    arr[i].append(val)
            def dp(total, arrEntry, index, target, cache):
                if total >= target:
                    if total == target:
                        return True
                    return False
                if index >= len(arrEntry):
                    return False
                if (total, index) in cache:
                    return cache[(total, index)]
                # dont take
                dontTake = dp(total, arrEntry, index+1, target, cache)
                doTake = dp(total+arrEntry[index], arrEntry, index+1, target, cache)
                cache[(total, index)] = dontTake or doTake
                return cache[(total, index)]
            res = True
            count = 0
            for arrEntry in arr:
                res = res and dp(0, arrEntry, 0, nums[count], {})
                count += 1
            if res:
                minK = mid
                right = mid - 1
            else:
                left = mid + 1
        return minK if minK < float('inf') else -1

