                for(int l=0; l<=val-decrement; ++l) {
                    dp[j][l] = min(dp[j][l],dp[j-1][l+decrement]);
                }
                if(dp[j][0]==0) {
                    maxValue = max(j,maxValue);
                    break;
                } else if(j==queries.size()) {
                    return -1;
                }
            }
        }
        return maxValue;
    }
};

