class Solution {
public:
    bool f(int index,int left_val,int query_idx,vector<vector<int>> &queries,int prefix,vector<vector<vector<int>>> &dp) {
        // cout<<left_val<<" "<<query_idx<<endl;
        if(query_idx>=prefix) {
            return (left_val == 0);
        }
        auto &x = dp[index][left_val][query_idx];
        if(x!=-1) {
            return x;
        }
        bool ans = false;
        // take it 
        if(queries[query_idx][0]<=index&&queries[query_idx][1]>=index&&queries[query_idx][2]<=left_val) {
            ans|=f(index,left_val-queries[query_idx][2],query_idx+1,queries,prefix,dp);
        }
        // leave it
        ans|=f(index,left_val,query_idx+1,queries,prefix,dp);
        return x = ans;
    }

    bool check(vector<int> &nums,vector<vector<int>> &queries,int prefix) {
        int maxx = *max_element(begin(nums),end(nums));
        int n = nums.size();
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(maxx+1,vector<int>(queries.size()+1,-1)));
        for(int i=0;i<n;i++) {
            if(f(i,nums[i],0,queries,prefix,dp)==false) {
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int i = 1,j = queries.size(),ans = -1;
        int maxx = *max_element(begin(nums),end(nums));
        if(maxx==0) {
            return 0;
        }
        while(i<=j) {
            int mid = (i+j)/2;
            if(check(nums,queries,mid)) {
                j=mid-1;
                ans=mid;
            } else {
                i=mid+1;
            }
        }
        return ans;
    }
};

