class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        Set<Integer>[] m = new HashSet[nums.length];
        for (int i = 0; i < nums.length; i++) {
            m[i] = new HashSet<>(Set.of(nums[i]));
        }
        if (ok(m)) {
            return 0;
        }
        for (int i = 0; i < queries.length; i++) {
            var q = queries[i];
            for (int j = q[0]; j <= q[1]; j++) {
                var s = new HashSet<>(m[j]);
                for (int l : m[j]) {
                    s.add(l - q[2]);
                }
                m[j] = s;
            }
            if (ok(m)) {
                return i + 1;
            }
        }
        return -1;
    }

    private boolean ok(Set<Integer>[] m) {
        for (int j = 0; j < m.length; j++) {
            if (!m[j].contains(0)) {
                return false;
            }
        }
        return true;
    }
}

