from collections import defaultdict


class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        n_q = len(queries)

        def can_sum(nums: list[int], val: int) -> bool:
            dp = [False] * (val + 1)
            dp[0] = True  #  0 

            for num in nums:
                for i in range(
                    val, num - 1, -1
                ):  # 
                    dp[i] = dp[i] or dp[i - num]

            return dp[val]

        def is_blue(k):
            d = defaultdict(list)

            for i in range(k):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    d[j].append(val)

            for i in range(n):
                if not can_sum(d[i], nums[i]):
                    return True

            return False

        l = -1
        r = n_q + 1
        while l + 1 != r:
            m = l + (r - l) // 2
            if is_blue(m):
                l = m
            else:
                r = m

        return r if r != n_q + 1 else -1

