from functools import lru_cache
from bisect import bisect_left
from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        def can(x, lst):
            """Check if x can be formed using elements in lst."""
            @lru_cache(None)
            def helper(i, x):
                if x == 0: return True
                if i == len(lst) or x < 0: return False
                return helper(i + 1, x - lst[i]) or helper(i + 1, x)

            result = helper(0, x)
            helper.cache_clear()
            return result

        def bcan(k):
            """Check if nums[0:k] can be formed using given queries."""
            c = [[] for _ in range(len(nums))]
            for l, r, v in queries[:k]:
                for i in range(l, r + 1):
                    c[i].append(v)

            return all(can(nums[i], c[i]) for i in range(len(nums)))

        idx =bisect_left(range(len(queries) + 1), True, key=bcan)
        return idx if idx<=len(queries) else -1

